                }
            }
            pos1 = pos3 - e.clientX;
            pos2 = pos4 - e.clientY;
            pos3 = e.clientX;
            pos4 = e.clientY;
            let x = Math.round((pos4 - pos2) / (size / 2) - 2) * (size / 2)
            let y = Math.round((pos3 - pos1) / (size / 2) - 2) * (size / 2)
            elmnt.style.top = x + "px";
            elmnt.style.left = y + "px";
            elmnt.style.zIndex = 998;
        }
