import { Link, useNavigate } from "react-router-dom"
import BasicDropdown from "./BasicDropdown";
import { useState } from "react";
import { useEffect } from "react";
import io from 'socket.io-client';
import Burger from "./Burger";
const PastLeaderboard = (inp) => {
    let navigate = useNavigate()
    document.body.style.background = '#292C2E'
    const socket = io('localhost:5000', { path: '/server' })
    socket.emit('get_past_dates')
    socket.on('connect', (data) => {

        socket.on('past_dates', (data) => {
            let temp = eval(data)
            for (let i = 0; i < temp.length; i++) {
                let div = document.createElement('button')
                
                div.style = "position: relative; border: 1px solid rgb(95, 99, 104); display: block; font-size: 4vh; width: 96%; margin-left: 2%; margin-top: 1%;"
                div.textContent = temp[i]
                div.addEventListener('click', () => {
                    period = temp[i]
                    document.querySelector('div#timePassed').textContent = temp[i]
                    socket.emit('get_past_data', inp.teacher, inp.period, period)
                })
                document.querySelector('div#periods').appendChild(div)
            }
        })
    })
    return (
        <div>
            <Burger></Burger>
            <p style={{ fontFamily: 'specialhelvetica', color: "white", marginTop: "4%", marginLeft: "1%", fontSize: "4vh", position: 'relative' }}>
                Past Discussions
            </p>
            <div id='container'>

            </div>

        </div>
    )
}

export default PastLeaderboard
