import { Link, useNavigate } from "react-router-dom"
import BasicDropdown from "./BasicDropdown";
import { useState } from "react";
import { useEffect } from "react";
import Burger from "./Burger";
import io from 'socket.io-client';
const Intermediate = () => {
    document.body.style.background = '#292C2E'
    const [hDropdown, sethDropdown] = useState(false)
    const [cDropdown, setcDropdown] = useState(false)
    const [discussions, setDiscussions] = useState({})
    const hughes_period = ["Period 2", "Period 4", "Period 6"];
    const ceran_period = ["Period 1", "Period 5"];
    const socket = io('localhost:5000', { path: '/server' })
    let navigate = useNavigate()
    useEffect(() => {
        document.querySelector("li[class='HughesPeriod 2']").addEventListener("click", function () {
            navigate('/past/hughes/2/')
        })
        document.querySelector("li[class='HughesPeriod 4']").addEventListener("click", function () {
            navigate('/past/hughes/4/')
        })
        document.querySelector("li[class='HughesPeriod 6']").addEventListener("click", function () {
            navigate('/past/hughes/6/')
        })
        document.querySelector("li[class='CeranPeriod 1']").addEventListener("click", function () {
            navigate('/past/ceran/1/')
        })
        document.querySelector("li[class='CeranPeriod 5']").addEventListener("click", function () {
            navigate('/past/ceran/5/')
        })

    })
    socket.on('connect', (data) => {
        socket.emit('get_discussions')
        socket.on('discussions', (data) => {
            var status = data
            let d = ["hughes_2", "hughes_4", "hughes_6", "ceran_1", "ceran_5"]
            for (var i in d) {
                if (d[i]['status'] == "active") {
                    setDiscussions((prevState) => ({
                        ...prevState,
                        J: {
                            "status": "a",
                        }
                    }))
                }
            }

        })
    })


    return (
        <div >
            <Burger></Burger>

            <p style={{ fontFamily: 'specialhelvetica', color: "white", marginTop: "4%", marginLeft: "1%", fontSize: "4vh" }}>
                Live Discussions
            </p>

            <button style={{ fontSize: "4vh", marginBottom: "1%", width: "96%", marginLeft: "2%" }} onClick={() => { navigate("/current/hughes/2/leaderboard") }}>
                Hughes Period 2
            </button>
            <button style={{ fontSize: "4vh", marginBottom: "1%", width: "96%", marginLeft: "2%" }} onClick={() => { navigate("/current/hughes/4/leaderboard") }}>
                Hughes Period 4
            </button>
            <button style={{ fontSize: "4vh", marginBottom: "1%", width: "96%", marginLeft: "2%" }} onClick={() => { navigate("/current/hughes/6/leaderboard") }}>
                Hughes Period 6
            </button>

            <button style={{ fontSize: "4vh", marginBottom: "1%", width: "96%", marginLeft: "2%" }} onClick={() => { navigate("/current/ceran/1/leaderboard") }}>
                Ceran Period 1
            </button>
            <button style={{ fontSize: "4vh", marginBottom: "1%", width: "96%", marginLeft: "2%" }} onClick={() => { navigate("/current/ceran/5/leaderboard") }}>
                Ceran Period 5
            </button>
            <p style={{ fontFamily: 'specialhelvetica', color: "white", marginLeft: "1%", fontSize: "4vh" }}>
                Past Discussions
            </p>
            <BasicDropdown
                showDropdown={hDropdown}
                setShowDropdown={() => sethDropdown(!hDropdown)}
                items={hughes_period}
                t="Hughes"
            />
            <BasicDropdown
                showDropdown={cDropdown}
                setShowDropdown={() => setcDropdown(!cDropdown)}
                items={ceran_period}
                t="Ceran"
            />
        </div>

    )
}

export default Intermediate;
