import React from "react";
import { useNavigate } from "react-router-dom";
import styled from 'styled-components';

let Ul = styled.ul
`.trigger-button {
  position: relative;
  text-align: center;
  margin-bottom: 2vh;
  width: 96vw;
  height: 4vh;
  background: #292C2E;
  color: #fff;
  font-size: 4vh;
  border: none;
  border-right: 1px solid #5f6368;
  /* width: 200px; */
}

.dropdown-wrapper {
  position: relative;
  margin-left: 2vw;
  border: 1px solid #5f6368;
  margin-top: 2vh;
  width: 96vw;
  z-index: 997;
  background: #292C2E;

}

.dropdown-wrapper .dropdown {
  background: #292C2E;
  position: relative;
  font-size: 3vh;
  display: flex;
  align-items: center;
}

ul.notactive {
  background: #292C2E;
  font-family: specialhelvetica;
  text-align: center;
  position: relative;
  list-style: none;
  font-size: 3vh;
  overflow: hidden;
  height: 0px;
  transition: height 0.3s ease;
  overflow-y: hidden
}

ul.active {
  font-family: specialhelvetica;
  text-align: center;
  background: #292C2E;
  list-style: none;
  font-size: 3vh;
  overflow: hidden;
  height: 13vh;
  overflow: auto;
  position: relative;
  transition: height 0.3s ease;
  overflow-y: hidden
}

li {
  position: relative;
  margin-bottom: 2px;
}

li:hover {
  position: relative;
  font-size: 4vh;
  cursor: pointer;
}
`
const BasicDropdown = ({ showDropdown, setShowDropdown, items, t }) => {
  let navigate = useNavigate()
  return (
    <Ul>
      <div className="dropdown-wrapper">
        <button onClick={setShowDropdown} className="trigger-button">
          {t}
        </button>
        <ul className={showDropdown ? "active" : "notactive"}>
          {items.map((item) => (
            <li className={t + item}>{item}</li>
          ))}
        </ul>
      </div>
    </Ul>
  );
};

export default BasicDropdown;
