import React from 'react';
import io from 'socket.io-client';
import Burger from './Burger';
import { useState } from 'react';
import Login from './Login';
import { useEffect } from 'react';
import styled from 'styled-components';
const Ul = styled.ul`
#overAll {
    width: 85vw;
    height: 85vh;
    // background-color: rgba(0, 0, 0, 0.955);
    margin-right: auto;
    margin-left: auto;
    margin: 0 auto;
    margin-top: 7.5vh;
    position: relative;
}

#welcomeAndTime {
    width: 25.26vw;
    height: 18.61vh;
    background-color: #292C2E;
    position: relative;
    z-index: 996;
    border-radius: 10px;
    border-style: solid;
    border-width: 2px;
    border-color: #373a3c;
}

#fag{
    position: absolute;
    right: 0;
    left: 0;
    top: 0;
    bottom: 0;
    margin: auto;
    height: min-content;
    width: min-content;
}

#time {
    z-index: 997;
    font-size: 30px;
    color: white;
    font-family: "specialhelvetica";
    display: inline-block;
    margin-left: 90px;
}

#date {
    z-index: 997;
    font-size: 30px;
    color: white;
    font-family: "specialhelvetica";
    right: 0px;
    left: auto;
    top: 0px;
    position: absolute;
    display: inline-block;
}

#greeting {
    z-index: 997;
    font-size: 30px;
    color: white;
    font-family: "specialhelvetica";
    display: flex;
    position: absolute;
    display: inline-block;
}

#previousDiscussions {
    width: 25.26vw;
    height: 65.35vh;
    background-color: #292C2E;
    position: absolute;
    z-index: 997;
    margin-left: 0px;
    bottom: 0px;
    border-radius: 10px;
    border-style: solid;
    border-width: 2px;
    border-color: #373a3c;
}

#controls {
    width: 59.375vw;
    height: 84.9vh;
    border-color: #373a3c;
    border-radius: 10px;
    border-width: 2px;
    border-style: solid;
    background-color: #292C2E;
    position: absolute;
    bottom: 0px;
    margin-left: 25.520833vw;
    z-index: 997;
}

#periodSelection {
    width: 21.145833vw;
    height: 9.97037vh;
    background-color: #292C2E;
    border-radius: 10px;
    border-style: solid;
    border-width: 2px;
    border-color: #373a3c;
    z-index: 997;
    position: absolute;
    margin-left: 0.2702083vw;
    margin-top: 0.481481vh;
}

#timePassed {
    width: 21.145833vw;
    height: 9.97037vh;
    background-color: #292C2E;
    border-radius: 10px;
    border-style: solid;
    border-width: 2px;
    border-color: #373a3c;
    z-index: 997;
    position: absolute;
    margin-left: 0.2702083vw;
    margin-top: 11.433332vh;
}

#chooseRecorder {
    width: 21.145833vw;
    height: 19.7251271vh;
    background-color: #292C2E;
    border-radius: 10px;
    border-style: solid;
    border-width: 2px;
    border-color: #373a3c;
    z-index: 997;
    position: absolute;
    margin-left: 0.2702083vw;
    margin-top: 22.4296278vh;
}

#startButton {
    width: 21.145833vw;
    height: 19.7251271vh;
    background-color: #292C2E;
    border-radius: 10px;
    border-style: solid;
    border-width: 2px;
    border-color: #373a3c;
    z-index: 997;
    position: absolute;
    margin-left: 0.2702083vw;
    margin-top: 43.0259236vh;
}

#stopButton {
    width: 21.145833vw;
    height: 19.7251271vh;
    background-color: #292C2E;
    border-radius: 10px;
    border-style: solid;
    border-width: 2px;
    border-color: #373a3c;
    z-index: 997;
    position: absolute;
    margin-left: 0.2702083vw;
    bottom: 0.5702083vh;
}
`
const Teacher = () => {
	document.body.style.background = '#292C2E'
	// const socket = io('http://54.151.75.108/', { path: '/server' });
	const socket = io('localhost:5000', { path: '/server' })
	const [aut, setAut] = useState("hidden")
	const [s, setS] = useState("visible")
	socket.on('connect', (data) => {
		window.addEventListener('storage', () => {
			// socket.emit('authentication', localStorage.getItem('name'))
			window.location.reload()
		});
		socket.emit('authentication', localStorage.getItem('name'))

		socket.on('auth', (data) => {
			if (data == "correct") {
				setAut('hidden')
				setS('visible')

				// document.querySelector('button.submit').addEventListener("click", function (event) {
				// 	event.stopPropagation()
				// 	socket.emit('spreadsheet')
				// })
			}
			else {
				setS('hidden')
				setAut('visible')
			}
		})

	})
	useEffect(() => {
		let teacherName = "Erol"
		let date = new Date();
		let teacherHours = date.getHours();
		let greeting = ""
		if (4 <= teacherHours && teacherHours <= 11) {
			greeting = "Good Morning " + teacherName + "!"
		} else if (11 < teacherHours && teacherHours <= 17) {
			greeting = "Good Afternoon " + teacherName + "!"
		} else {
			greeting = "Good Evening " + teacherName
		};
		document.getElementById("greeting").innerHTML = greeting
		let fucker = new Date();
		let year = JSON.stringify(fucker.getFullYear())
		year = year.slice(2, 4)
		document.getElementById("date").innerHTML = fucker.getMonth() + 1 + "/" + fucker.getDate() + "/" + year
		console.log(date.getMonth())
		setInterval(function () {
			let date = new Date();
			let hours = date.getHours()
			let AMPM = "AM"
			let timeMinutes = date.getMinutes()
			if (hours >= 12) {
				hours -= 12
				AMPM = "PM"
			}
			if (hours == 0) {
				hours = 12
				AMPM = "AM"
			}
			if (timeMinutes < 10) {
				timeMinutes = "0" + timeMinutes
			}
			document.getElementById("time").innerHTML = hours + ":" + timeMinutes + " " + AMPM
		}, 1000)
	})
	return (
		<div>
			<Ul>
				<Burger></Burger>
				<div id="overAll">
					<div id="welcomeAndTime">
						<div id="fag">
							<div id="time">
								<p></p>
							</div>
							<div id="date">
								<p></p>
							</div>
							<div id="greeting">
								<p></p>
							</div>
						</div>
					</div>
					<div id="previousDiscussions"></div>
					<div id="controls">
						<div id="periodSelection"></div>
						<div id="timePassed"></div>
						<div id="chooseRecorder"></div>
						<div id="startButton"></div>
						<div id="stopButton"></div>
					</div>
				</div>
				<h1 style={{ visibility: aut, position: "fixed", top: "10%", left: "50%", transform: "translate(-50%, -50%)", fontSize: "10vh", zIndex: 998 }}>Wrong Password</h1>
				<img src="https://media.tenor.com/s6Y_D_8viO4AAAAC/clash-royale-cry.gif" style={{ visibility: aut, height: "60vh", position: "fixed", top: "50%", left: "50%", transform: "translate(-50%, -50%)", zIndex: 998 }}></img>

				{/* <button className="submit" style={{ visibility: s, height: "60vh", width: "30vw", marginLeft: "40vw", marginTop: "20vh" }}>Push me to enter discussion data into a google spreadsheet</button> */}

			</Ul >
		</div >

	);
}

export default Teacher;
