const Teacher = () => {
    document.body.style.background = '#292C2E'
    // const socket = io('http://54.151.75.108/', { path: '/server' });
    const socket = io('localhost:5000', { path: '/server' })
    const [aut, setAut] = useState("hidden")
    const [s, setS] = useState("visible")
    socket.on('connect', (data) => {
        window.addEventListener('storage', () => {
            // socket.emit('authentication', localStorage.getItem('name'))
            window.location.reload()
        });
        socket.emit('authentication', localStorage.getItem('name'))

        socket.on('auth', (data) => {
            if (data == "correct") {
                setAut('hidden')
                setS('visible')

                // document.querySelector('button.submit').addEventListener("click", function (event) {
                // 	event.stopPropagation()
                // 	socket.emit('spreadsheet')
                // })
            }
            else {
                setS('hidden')
                setAut('visible')
            }
        })

    })
    useEffect(() => {
        let teacherName = "Erol"
        let date = new Date();
        let teacherHours = date.getHours();
        let greeting = ""
        if (4 <= teacherHours && teacherHours <= 11) {
            greeting = "Good Morning " + teacherName + "!"
        } else if (11 < teacherHours && teacherHours <= 17) {
            greeting = "Good Afternoon " + teacherName + "!"
        } else {
            greeting = "Good Evening " + teacherName
        };
        document.getElementById("greeting").innerHTML = greeting
        let d = new Date();
        let year = JSON.stringify(d.getFullYear())
        year = year.slice(2, 4)
        document.getElementById("date").innerHTML = d.getMonth() + 1 + "/" + d.getDate() + "/" + year
        console.log(date.getMonth())
        setInterval(function () {
            let date = new Date();
            let hours = date.getHours()
            let AMPM = "AM"
            let timeMinutes = date.getMinutes()
            if (hours >= 12) {
                hours -= 12
                AMPM = "PM"
            }
            if (hours == 0) {
                hours = 12
                AMPM = "AM"
            }
            if (timeMinutes < 10) {
                timeMinutes = "0" + timeMinutes
            }
            document.getElementById("time").innerHTML = hours + ":" + timeMinutes + " " + AMPM
        }, 1000)
    })
    return (
        <div>
            <Ul>
                <Burger></Burger>
                <div id="overAll">
                    <div id="welcomeAndTime">
                        <div id="time">
                            <p></p>
                        </div>
                        <div id="date">
                            <p></p>
                        </div>
                        <div id="greeting">
                            <p></p>
                        </div>
                    </div>
                    <div id="previousDiscussions"></div>
                    <div id="controls">
                        <div id="periodSelection"></div>
                        <div id="timePassed"></div>
                        <div id="chooseRecorder"></div>
                        <div id="startButton"></div>
                        <div id="stopButton"></div>
                    </div>
                </div>
                <h1 style={{ visibility: aut, position: "fixed", top: "10%", left: "50%", transform: "translate(-50%, -50%)", fontSize: "10vh", zIndex: 998 }}>Wrong Password</h1>
                <img src="https://media.tenor.com/s6Y_D_8viO4AAAAC/clash-royale-cry.gif" style={{ visibility: aut, height: "60vh", position: "fixed", top: "50%", left: "50%", transform: "translate(-50%, -50%)", zIndex: 998 }}></img>

                {/* <button className="submit" style={{ visibility: s, height: "60vh", width: "30vw", marginLeft: "40vw", marginTop: "20vh" }}>Push me to enter discussion data into a google spreadsheet</button> */}

            </Ul >
        </div >

    );
}

export default Teacher;
